        sort = sort1.join(',')
    }
    if (!/^\s*$/.test(sort)) {
        sort = String(sort).split(',');
        f = sort.length > 1 ? '分组' : '分类';
        V = v[sort[0]];
        if (name == '全部分类') v = {};
        login('【更改成功】\n『' + f + '』 ' + name + ': 已' + (V ? '禁用' : '启用'));
        sort.map(key => v[key] = V ? 0 : 1);
        return set(v);
    }
    return login('【无法更改】\n原因：没有有效分类(分类名称不正确)')
}

